pragma solidity ^0.8.17;
pragma experimental ABIEncoderV2;

import { SafeMath } from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

/**
 * @title FeesManager
 * @dev Contract is responsible for fees calculations.
 */
contract FeesManager is OwnableUpgradeable {
    using SafeMath for uint256;

    // State

    /**
     * @dev Factor of fees.
     * @notice a value of 10000 means that 223 equals 2.23% and 10000 equals 100%
     */
    uint256 public constant PRECISION = 10000;

    uint256 private depositFee;
    uint256 private depositBridgeFee;
    uint256 private withdrawalFee;
    uint256 private withdrawalBridgeFee;

    // Events

    /**
     * @dev Emitted when deposit fee has changed.
     * @param depositFee            Amount of the fee.
     */
    event DepositFeeChanged(uint256 depositFee);

    /**
     * @dev Emitted when deposit bridge fee has changed.
     * @param depositBridgeFee      Amount of the fee.
     */
    event DepositBridgeFeeChanged(uint256 depositBridgeFee);

    /**
     * @dev Emitted when withdrawal fee has changed.
     * @param withdrawalFee         Amount of the fee.
     */
    event WithdrawalFeeChanged(uint256 withdrawalFee);

    /**
     * @dev Emitted when withdrawal bridge fee has changed.
     * @param withdrawalBridgeFee   Amount of the fee.
     */
    event WithdrawalBridgeFeeChanged(uint256 withdrawalBridgeFee);

    // Initializer

    /**
     * @dev Contract initializer.
     * @param _depositFee           Amount of deposit fee in promils.
     * @param _depositBridgeFee     Amount of deposit through bridge fee in promils.
     * @param _withdrawalFee        Amount of redeem fee in promils.
     * @param _withdrawalBridgeFee  Amount of redeem through bridge fee in promils.
     */
    function initialize(
        uint256 _depositFee,
        uint256 _depositBridgeFee,
        uint256 _withdrawalFee,
        uint256 _withdrawalBridgeFee
    ) external initializer {
        __Ownable_init_unchained();
        setDepositFee(_depositFee);
        setDepositBridgeFee(_depositBridgeFee);
        setWithdrawalFee(_withdrawalFee);
        setWithdrawalBridgeFee(_withdrawalBridgeFee);
    }

    // Internal

    /**
     * @dev Calculate and return fee amount based on massetAmount and type of fee.
     */
    function _calculateFee(uint256 _massetAmount, uint256 _fee) internal pure returns (uint256) {
        return _massetAmount.mul(_fee).div(PRECISION);
    }

    // Public

    /**
     * @dev Calculate and return deposit fee amount based on massetAmount.
     * @param _massetAmount  Amount of masset to deposit.
     * @return fee           Calculated fee amount.
     */
    function calculateDepositFee(uint256 _massetAmount) external view returns (uint256) {
        return _calculateFee(_massetAmount, depositFee);
    }

    /**
     * @dev Calculate and return fee for deposit through bridge.
     * @param _massetAmount  Amount of masset to deposit.
     * @return fee           Calculated fee amount.
     */
    function calculateDepositBridgeFee(uint256 _massetAmount) external view returns (uint256) {
        return _calculateFee(_massetAmount, depositBridgeFee);
    }

    /**
     * @dev Calculate and return redeem fee amount based on massetAmount.
     * @param _massetAmount  Amount of masset.
     * @return fee           Calculated fee amount.
     */
    function calculateRedeemFee(uint256 _massetAmount) external view returns (uint256) {
        return _calculateFee(_massetAmount, withdrawalFee);
    }

    /**
     * @dev Calculate and return fee for redeem through bridge.
     * @param _massetAmount  Amount of masset.
     * @return fee           Calculated fee amount.
     */
    function calculateRedeemBridgeFee(uint256 _massetAmount) external view returns (uint256) {
        return _calculateFee(_massetAmount, withdrawalBridgeFee);
    }

    // Getters

    function getDepositFee() external view returns (uint256) {
        return depositFee;
    }

    function getDepositBridgeFee() external view returns (uint256) {
        return depositBridgeFee;
    }

    function getWithdrawalFee() external view returns (uint256) {
        return withdrawalFee;
    }

    function getWithdrawalBridgeFee() external view returns (uint256) {
        return withdrawalBridgeFee;
    }

    // Governance methods

    function setDepositFee(uint256 _amount) public onlyOwner {
        require(_amount <= PRECISION, "invalid fee amount");

        depositFee = _amount;
        emit DepositFeeChanged(_amount);
    }

    function setDepositBridgeFee(uint256 _amount) public onlyOwner {
        require(_amount <= PRECISION, "invalid fee amount");

        depositBridgeFee = _amount;
        emit DepositBridgeFeeChanged(_amount);
    }

    function setWithdrawalFee(uint256 _amount) public onlyOwner {
        require(_amount <= PRECISION, "invalid fee amount");

        withdrawalFee = _amount;
        emit WithdrawalFeeChanged(_amount);
    }

    function setWithdrawalBridgeFee(uint256 _amount) public onlyOwner {
        require(_amount <= PRECISION, "invalid fee amount");

        withdrawalBridgeFee = _amount;
        emit WithdrawalBridgeFeeChanged(_amount);
    }
}
","keccak256":"0x874d0f2802c5f0f521ce09a5ddc0a7c23b31e335a3432d52ae5b75f63514427a","license":"MIT"},"contracts/masset/IBridge.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

/**
 * @title IBridge
 * @dev Interface of the bridge. Bridge is used to exchange basset tokens from another blockchains.
 */

interface IBridge {
    /**
     * @dev Returns the version.
     */
    function version() external pure returns (string memory);

    /**
     * @dev Returns the fee percentage.
     */
    function getFeePercentage() external view returns (uint);

    /**
     * @dev Calculates maximal withdraw.
     */
    function calcMaxWithdraw() external view returns (uint);

    /**
     * @dev ERC-20 tokens approve and transferFrom pattern
     * See https://eips.ethereum.org/EIPS/eip-20#transferfrom for details.
     */
    function receiveTokens(address tokenToUse, uint256 amount) external returns (bool);

    /**
     * @dev ERC-20 tokens approve and transferFrom pattern
     * See https://eips.ethereum.org/EIPS/eip-20#transferfrom for details.
     */
    function receiveTokensAt(
        address tokenToUse,
        uint256 amount,
        address receiver,
        bytes calldata extraData
    ) external returns (bool);

    /**
     * @dev ERC-777 tokensReceived hook allows to send tokens to a contract and notify it in a single transaction
     * See https://eips.ethereum.org/EIPS/eip-777#motivation for details.
     */
    function tokensReceived(
        address operator,
        address from,
        address to,
        uint256 amount,
        bytes calldata userData,
        bytes calldata operatorData
    ) external;

    /**
     * @dev Accepts the transaction from the other chain that was voted and sent by the federation contract.
     * @return Returns a boolean value indicating whether transfer was accepted.
     */
    function acceptTransfer(
        address originalTokenAddress,
        address receiver,
        uint256 amount,
        string calldata symbol,
        bytes32 blockHash,
        bytes32 transactionHash,
        uint32 logIndex,
        uint8 decimals,
        uint256 granularity
    ) external returns (bool);

    /**
     * @dev Accepts the transaction from the other chain that was voted and sent by the federation contract.
     * @return Returns a boolean value indicating whether transfer was accepted.
     */
    function acceptTransferAt(
        address originalTokenAddress,
        address receiver,
        uint256 amount,
        string calldata symbol,
        bytes32 blockHash,
        bytes32 transactionHash,
        uint32 logIndex,
        uint8 decimals,
        uint256 granularity,
        bytes calldata userData
    ) external returns (bool);

    /**
     * @dev Emitted when cross occured.
     */
    event Cross(
        address indexed _tokenAddress,
        address indexed _to,
        uint256 _amount,
        string _symbol,
        bytes _userData,
        uint8 _decimals,
        uint256 _granularity
    );

    /**
     * @dev Emitted when new side token is deployed.
     */
    event NewSideToken(
        address indexed _newSideTokenAddress,
        address indexed _originalTokenAddress,
        string _newSymbol,
        uint256 _granularity
    );

    /**
     * @dev Emitted when cross transfer is accepted.
     */
    event AcceptedCrossTransfer(
        address indexed _tokenAddress,
        address indexed _to,
        uint256 _amount,
        uint8 _decimals,
        uint256 _granularity,
        uint256 _formattedAmount,
        uint8 _calculatedDecimals,
        uint256 _calculatedGranularity,
        bytes _userData
    );

    /**
     * @dev Emitted when fee percentage has changed.
     */
    event FeePercentageChanged(uint256 _amount);

    /**
     * @dev Emitted when error while receiving token occured.
     */
    event ErrorTokenReceiver(bytes _errorData);
}
","keccak256":"0x43deadb853637363f2dc80a0a8e869893a53df3cd96557a9724b6182faacb33e","license":"MIT"},"contracts/masset/MassetManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;
pragma experimental ABIEncoderV2;

import { SafeMath } from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC777Recipient } from "@openzeppelin/contracts/token/ERC777/IERC777Recipient.sol";
import { IERC1820Registry } from "@openzeppelin/contracts/utils/introspection/IERC1820Registry.sol";
import { IBridge } from "./IBridge.sol";
import { BasketManagerV3 } from "./BasketManagerV3.sol";
import { FeesVault } from "../vault/FeesVault.sol";
import { FeesManager } from "./FeesManager.sol";
import "../meta-asset-token/MetaAssetToken.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/ERC1967/ERC1967UpgradeUpgradeable.sol";

/**
 * @title MassetManager
 * @dev Contract is responsible for managing mAsset and bAsset.
 * Used for minting and burning tokens, calculating fees and calling the bridge
 * if transaction based on token from another blockchain.
 */

contract MassetManager is
    IERC777Recipient,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    ERC1967UpgradeUpgradeable
{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using SafeERC20 for MetaAssetToken;

    // events

    /**
     * @dev Emitted when deposit is completed.
     * @param minter            Address of the minter.
     * @param recipient         Address of the recipient.
     * @param massetQuantity    Masset quantity.
     * @param bAsset            Address of the bAsset.
     * @param bassetQuantity    Basset quantity.
     */
    event Minted(
        address indexed minter,
        address indexed recipient,
        uint256 massetQuantity,
        address bAsset,
        uint256 bassetQuantity
    );

    /**
     * @dev Emitted when withdrawal is completed.
     * @param redeemer          Address of the redeemer.
     * @param recipient         Address of the recipient.
     * @param massetQuantity    Masset quantity.
     * @param bAsset            Address of the bAsset.
     * @param bassetQuantity    Basset quantity.
     */
    event Redeemed(
        address indexed redeemer,
        address indexed recipient,
        uint256 massetQuantity,
        address bAsset,
        uint256 bassetQuantity
    );

    /**
     * @dev Emitted when tokensReceived method is called by the bridge.
     * @param operator         Address operator requesting the transfer.
     * @param from             Address token holder address.
     * @param to               Address recipient address.
     * @param amount           uint256 amount of tokens to transfer.
     * @param userData         Bytes extra information provided by the token holder (if any).
     * @param operatorData     Bytes extra information provided by the operator (if any).
     */
    event onTokensReceivedCalled(
        address operator,
        address from,
        address to,
        uint256 amount,
        bytes userData,
        bytes operatorData
    );

    /**
     * @dev Emitted when onTokensMinted method is called by the bridge.
     * @param sender           Address of the sender.
     * @param orderAmount      Units of the masset to redeem.
     * @param tokenAddress     Address of the bAsset to redeem.
     * @param userData         Address of the final recipient as ABI encoded bytes.
     */
    event onTokensMintedCalled(
        address indexed sender,
        uint256 orderAmount,
        address tokenAddress,
        bytes userData
    );

    // state

    bytes32 constant ERC777_RECIPIENT_INTERFACE_HASH = keccak256("ERC777TokensRecipient");

    string private version;

    BasketManagerV3 private basketManager;
    MetaAssetToken private mAssetToken;

    FeesVault private feesVault;
    FeesManager private feesManager;

    // internal

    /**
     * @dev Register this contracts as implementer of the "ERC777 Tokens Recipient" interface in the ERC1820 registry.
     */
    function registerAsERC777Recipient() internal {
        IERC1820Registry ERC1820 = IERC1820Registry(0x1820a4B7618BdE71Dce8cdc73aAB6C95905faD24);
        ERC1820.setInterfaceImplementer(
            address(this),
            ERC777_RECIPIENT_INTERFACE_HASH,
            address(this)
        );
    }

    // public

    /**
     * @dev Contract initializer.
     * @param _basketManagerAddress           Address of the basket manager.
     * @param _mAssetTokenAddress             Address of the mAsset mAssetToken.
     * @param _registerAsERC777RecipientFlag  Bool determine if contract should be register as ERC777 recipient.
     */
    function initialize(
        address _basketManagerAddress,
        address _mAssetTokenAddress,
        bool _registerAsERC777RecipientFlag
    ) public initializer {
        require(
            address(basketManager) == address(0) && address(mAssetToken) == address(0),
            "already initialized"
        );
        require(_basketManagerAddress != address(0), "invalid basket manager");
        require(_mAssetTokenAddress != address(0), "invalid mAssetToken");

        __Ownable_init_unchained();
        __ReentrancyGuard_init_unchained();

        basketManager = BasketManagerV3(_basketManagerAddress);
        mAssetToken = MetaAssetToken(_mAssetTokenAddress);
        if (_registerAsERC777RecipientFlag) {
            registerAsERC777Recipient();
        }

        version = "1.0";
    }

    /***************************************
                MINTING (PUBLIC)
    ****************************************/

    /**
     * @dev Mint a single mAsset, at a 1:1 ratio with the bAsset. This contract
     *      must have approval to spend the senders bAsset.
     * @param _bAsset         Address of the bAsset.
     * @param _bAssetQuantity Quantity in bAsset units.
     * @return massetMinted   Quantity of newly minted mAsset.
     */
    function mint(
        address _bAsset,
        uint256 _bAssetQuantity
    ) external nonReentrant returns (uint256 massetMinted) {
        return _mintTo(_bAsset, _bAssetQuantity, msg.sender);
    }

    /**
     * @dev Mint a single mAsset to recipient address, at a 1:1 ratio with the bAsset.
     *      This contract must have approval to spend the senders bAsset.
     * @param _bAsset         Address of the bAsset.
     * @param _bAssetQuantity Quantity in bAsset units.
     * @param _recipient      Receipient of the newly minted mAsset tokens.
     * @return massetMinted   Number of newly minted mAssets.
     */
    function mintTo(
        address _bAsset,
        uint256 _bAssetQuantity,
        address _recipient
    ) external nonReentrant returns (uint256 massetMinted) {
        return _mintTo(_bAsset, _bAssetQuantity, _recipient);
    }

    /***************************************
              MINTING (INTERNAL)
    ****************************************/

    /**
     * @dev Mint a single mAsset to recipient address, at a 1:1 ratio with the bAsset.
     *      This contract must have approval to spend the senders bAsset.
     * @param _basset         Address of the bAsset.
     * @param _bassetQuantity Quantity in bAsset units.
     * @param _recipient      Receipient of the newly minted mAsset tokens.
     * @return massetMinted   Number of newly minted mAssets.
     */
    function _mintTo(
        address _basset,
        uint256 _bassetQuantity,
        address _recipient
    ) internal returns (uint256 massetMinted) {
        require(_recipient != address(0), "must be a valid recipient");
        require(_bassetQuantity > 0, "quantity must not be 0");

        require(basketManager.isValidBasset(_basset), "invalid basset");

        require(
            basketManager.checkBasketBalanceForDeposit(_basset, _bassetQuantity),
            "invalid basket"
        );

        (uint256 massetQuantity, uint256 bassetQuantity) = basketManager
            .convertBassetToMassetQuantity(_basset, _bassetQuantity);

        IERC20(_basset).safeTransferFrom(msg.sender, address(this), bassetQuantity);

        uint256 massetsToMint = _mintAndCalculateFee(massetQuantity, false);
        mAssetToken.mint(_recipient, massetsToMint);

        emit Minted(msg.sender, _recipient, massetsToMint, _basset, bassetQuantity);

        return massetsToMint;
    }

    /**
     * @dev Mints fee to vault contract and return the amount of massets that goes to the user.
     * @param massetQuantity    Amount of massets.
     * @param _bridgeFlag       Flag that indicates if the proces is used with conjunction with bridge.
     * @return massetsToMint    Amount of massets that is left to mint for user.
     */
    function _mintAndCalculateFee(
        uint256 massetQuantity,
        bool _bridgeFlag
    ) internal returns (uint256 massetsToMint) {
        uint256 fee;
        if (_bridgeFlag) {
            fee = feesManager.calculateDepositBridgeFee(massetQuantity);
        } else {
            fee = feesManager.calculateDepositFee(massetQuantity);
        }

        massetsToMint = massetQuantity.sub(fee);

        mAssetToken.mint(address(feesVault), fee);

        return massetsToMint;
    }

    /***************************************
              REDEMPTION (PUBLIC)
    ****************************************/

    /**
     * @dev Credits the sender with a certain quantity of selected bAsset, in exchange for burning the
     *      relative mAsset quantity from the sender. Sender also incurs a small mAsset fee, if any.
     * @param _bAsset           Address of the bAsset to redeem.
     * @param _massetQuantity   Units of the masset to redeem.
     * @return massetRedeemed   Relative number of mAsset units burned to pay for the bAssets.
     */
    function redeem(
        address _bAsset,
        uint256 _massetQuantity
    ) external nonReentrant returns (uint256 massetRedeemed) {
        return _redeemTo(_bAsset, _massetQuantity, msg.sender, false, false);
    }

    /**
     * @dev Credits a recipient with a certain quantity of selected bAsset, in exchange for burning the
     *      relative mAsset quantity from the sender. Sender also incurs a small fee, if any.
     * @param _bAsset           Address of the bAsset to redeem.
     * @param _massetQuantity   Units of the masset to redeem.
     * @param _recipient        Address to credit with withdrawn bAssets.
     * @return massetRedeemed   Relative number of mAsset units burned to pay for the bAssets.
     */
    function redeemTo(
        address _bAsset,
        uint256 _massetQuantity,
        address _recipient
    ) external nonReentrant returns (uint256 massetRedeemed) {
        return _redeemTo(_bAsset, _massetQuantity, _recipient, false, false);
    }

    /***************************************
              REDEMPTION (INTERNAL)
    ****************************************/

    /**
     * @dev Credits a recipient with a certain quantity of selected bAsset, in exchange for burning the
     *      relative mAsset quantity from the sender. Sender also incurs a small fee, if any.
     * @param _basset           Address of the bAsset to redeem.
     * @param _massetQuantity   Units of the masset to redeem.
     * @param _recipient        Address to credit with withdrawn bAssets.
     * @param _bridgeFlag       Flag that indicates if the reedem proces is used with conjunction with bridge.
     * @param _useCallback      Flag that indicates if this method should call onTokensMinted in case of usage of bridge.
     * @return massetRedeemed     Relative number of mAsset units burned to pay for the bAssets.
     */
    function _redeemTo(
        address _basset,
        uint256 _massetQuantity,
        address _recipient,
        bool _bridgeFlag,
        bool _useCallback
    ) internal returns (uint256 massetRedeemed) {
        require(_recipient != address(0), "must be a valid recipient");
        require(_massetQuantity > 0, "mAsset quantity must be greater than 0");
        require(basketManager.isValidBasset(_basset), "invalid basset");

        address massetSource = (_bridgeFlag && !_useCallback) ? _recipient : msg.sender;

        // massetsToBurn is the amount of massets that is left to burn after the fee was taken.
        // It is used to calculate amount of bassets that are transfered to user.
        uint256 massetsAfterFee = _transferAndCalulateFee(
            _massetQuantity,
            massetSource,
            _bridgeFlag
        );
        (uint256 bassetQuantity, uint256 massetsToBurn) = basketManager
            .convertMassetToBassetQuantity(_basset, massetsAfterFee);

        require(
            basketManager.checkBasketBalanceForWithdrawal(_basset, bassetQuantity),
            "invalid basket"
        );

        mAssetToken.burn(massetSource, massetsToBurn);
        // In case of withdrawal to bridge the receiveTokensAt is called instead of transfer.
        if (_bridgeFlag && _useCallback) {
            address bridgeAddress = basketManager.getBridge(_basset);
            require(bridgeAddress != address(0), "invalid bridge");

            IERC20(_basset).approve(bridgeAddress, bassetQuantity);
            require(
                IBridge(bridgeAddress).receiveTokensAt(
                    _basset,
                    bassetQuantity,
                    _recipient,
                    bytes("")
                ),
                "call to bridge failed"
            );
        } else {
            IERC20(_basset).safeTransfer(_recipient, bassetQuantity);
        }

        emit Redeemed(massetSource, _recipient, _massetQuantity, _basset, bassetQuantity);

        return massetsToBurn;
    }

    /**
     * @dev Transfers fee to vault contract and return the amount of massets that will be burned
     *      must have approval to spend the sender's mAasset.
     * @param massetQuantity        Amount of massets to withdraw.
     * @param sender                Owner of massets.
     * @param _bridgeFlag           Flag that indicates if the proces is used with conjunction with bridge.
     * @return massetsToBurn        Amount of massets that is left to burn.
     */
    function _transferAndCalulateFee(
        uint256 massetQuantity,
        address sender,
        bool _bridgeFlag
    ) internal returns (uint256 massetsToBurn) {
        uint256 fee;
        if (_bridgeFlag) {
            fee = feesManager.calculateRedeemBridgeFee(massetQuantity);
        } else {
            fee = feesManager.calculateRedeemFee(massetQuantity);
        }

        massetsToBurn = massetQuantity.sub(fee);

        mAssetToken.safeTransferFrom(sender, address(feesVault), fee);

        return massetsToBurn;
    }

    // For the BRIDGE

    /**
     * @dev Credits a recipient with a certain quantity of selected bAsset, in exchange for burning the
     *      relative mAsset quantity from the sender. Sender also incurs a small fee, if any.
     *      This function is designed to also call the bridge in order to have the basset tokens sent to
     *      another blockchain.
     * @param _basset           Address of the bAsset to redeem.
     * @param _massetQuantity   Units of the mAsset to redeem.
     * @param _recipient        Address to credit with withdrawn bAssets.
     * @param _bridgeAddress    This is ignored and is left here for backward compatibility with the FE.
     * @return massetRedeemed   Relative number of mAsset units burned to pay for the bAssets.
     */
    function redeemToBridge(
        address _basset,
        uint256 _massetQuantity,
        address _recipient,
        address _bridgeAddress // IGNORED! for backward compatibility
    ) external nonReentrant returns (uint256 massetRedeemed) {
        return _redeemTo(_basset, _massetQuantity, _recipient, true, true);
    }

    /**
     * @dev Credits a recipient with a certain quantity of selected bAsset, in exchange for burning the
     *      relative mAsset quantity from the sender. Sender also incurs a small fee, if any.
     *      This function is designed to also call the bridge in order to have the basset tokens sent to
     *      another blockchain.
     * @param _basset           Address of the bAsset to redeem.
     * @param _massetQuantity   Units of the mAsset to redeem.
     * @param _recipient        Address to credit with withdrawn bAssets.
     * @return massetRedeemed   Relative number of mAsset units burned to pay for the bAssets.
     */
    function redeemToBridge(
        address _basset,
        uint256 _massetQuantity,
        address _recipient
    ) external nonReentrant returns (uint256 massetRedeemed) {
        return _redeemTo(_basset, _massetQuantity, _recipient, true, true);
    }

    /**
     * @dev Credits a recipient with a certain quantity of selected bAsset, in exchange for burning the
     *      relative mAsset quantity from the sender. Sender also incurs a small fee, if any.
     *      This function is designed to be called by the bridge in order to have diffrent fees.
     * @param _basset           Address of the bAsset to redeem.
     * @param _massetQuantity   Units of the mAsset to redeem.
     * @param _recipient        Address to credit with withdrawn bAssets.
     * @return massetRedeemed     Relative number of mAsset units burned to pay for the bAssets.
     */
    function redeemByBridge(
        address _basset,
        uint256 _massetQuantity,
        address _recipient
    ) external nonReentrant returns (uint256 massetRedeemed) {
        address bridgeAddress = basketManager.getBridge(_basset);
        require(bridgeAddress != address(0), "invalid bridge");
        require(bridgeAddress == msg.sender, "must be called by bridge");

        return _redeemTo(_basset, _massetQuantity, _recipient, true, false);
    }

    /**
     * @dev Decode bytes data to address.
     * @param data              Data to decode.
     * @return address          Decoded address.
     */
    function _decodeAddress(bytes memory data) private pure returns (address) {
        address addr = abi.decode(data, (address));
        require(addr != address(0), "Converter: Error decoding extraData");
        return addr;
    }

    /**
     * @dev Encode address to bytes data.
     * @param _address          Address to encode.
     * @return address          Decoded address.
     */
    function _encodeAddress(address _address) private pure returns (bytes memory) {
        require(_address != address(0), "Converter: Error encoding extraData");
        return abi.encode(_address);
    }

    /**
     * @dev This is called by the bridge to let us know tokens have been received.
     * @param _operator         Address operator requesting the transfer.
     * @param _from             Address token holder address.
     * @param _to               Address recipient address.
     * @param _amount           uint256 amount of tokens to transfer.
     * @param _userData         Bytes extra information provided by the token holder (if any).
     * @param _operatorData     Bytes extra information provided by the operator (if any).
     */
    function tokensReceived(
        address _operator,
        address _from,
        address _to,
        uint256 _amount,
        bytes calldata _userData,
        bytes calldata _operatorData
    ) external {
        emit onTokensReceivedCalled(_operator, _from, _to, _amount, _userData, _operatorData);
    }

    /**
     * @dev This is called by the bridge to let us know the user has sent tokens through it and
     *      into the mAsset.
     * @param _orderAmount      Units of the mAsset to redeem.
     * @param _tokenAddress     Address of the bAsset to redeem.
     * @param _userData         Address of the final recipient as ABI encoded bytes.
     */
    function onTokensMinted(
        uint256 _orderAmount,
        address _tokenAddress,
        bytes calldata _userData
    ) external nonReentrant {
        emit onTokensMintedCalled(msg.sender, _orderAmount, _tokenAddress, _userData);

        require(_orderAmount > 0, "amount must be > 0");

        address recipient = _decodeAddress(_userData);
        address basset = _tokenAddress;

        address bridgeAddress = basketManager.getBridge(basset);
        require(msg.sender == bridgeAddress, "only bridge may call");

        require(basketManager.isValidBasset(basset), "invalid basset");
        require(
            basketManager.checkBasketBalanceForDeposit(basset, _orderAmount),
            "basket out of balance"
        );

        (uint256 massetQuantity, uint256 bassetQuantity) = basketManager
            .convertBassetToMassetQuantity(basset, _orderAmount);
        uint256 massetsToMint = _mintAndCalculateFee(massetQuantity, true);
        mAssetToken.mint(recipient, massetsToMint);

        emit Minted(msg.sender, recipient, massetsToMint, basset, bassetQuantity);
    }

    // Getters

    function getFeesVault() external view returns (address) {
        return address(feesVault);
    }

    function getFeesManager() external view returns (address) {
        return address(feesManager);
    }

    function getVersion() external view returns (string memory) {
        return version;
    }

    function getToken() external view returns (address) {
        return address(mAssetToken);
    }

    function getBasketManager() external view returns (address) {
        return address(basketManager);
    }

    // v3 migration
    /**
     * @dev Migration to V3 version.
     * @param _basketManagerAddress     Address of new BasketManagerV3.
     * @param _tokenAddress             Address of mAsset token.
     * @param _feesVaultAddress         Address of FeesVault contract.
     * @param _feesManagerAddress       Adress of FeesManager contract.
     */
    function upgradeToV3(
        address _basketManagerAddress,
        address _tokenAddress,
        address _feesVaultAddress,
        address _feesManagerAddress
    ) external {
        require(
            keccak256(bytes(version)) == keccak256(bytes("1.0")) ||
                keccak256(bytes(version)) == keccak256(bytes("2.0")),
            "wrong version (1)"
        );
        require(
            keccak256(bytes(BasketManagerV3(_basketManagerAddress).getVersion())) ==
                keccak256(bytes("3.0")),
            "wrong version (2)"
        );
        require(_feesVaultAddress != address(0), "invalid vault address");
        require(_feesManagerAddress != address(0), "invalid fees manager address");

        feesVault = FeesVault(_feesVaultAddress);
        feesManager = FeesManager(_feesManagerAddress);
        basketManager = BasketManagerV3(_basketManagerAddress);
        mAssetToken = MetaAssetToken(_tokenAddress);
        version = "3.0";
    }

    /**
     * @dev get the implementation logic address referring to ERC1967 standard.
     *
     * @return logic implementation address.
     */
    function getProxyImplementation() external view returns (address) {
        return ERC1967UpgradeUpgradeable._getImplementation();
    }
}
","keccak256":"0xce092bf9e7d7710443f179c92620cf50135b91b5d1073cf3f0e0eb0cbf14281e","license":"MIT"},"contracts/meta-asset-token/MetaAssetToken.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/draft-ERC20Permit.sol";
import "../interfaces/IApproveAndCall.sol";
import "../interfaces/IProxy.sol";

/**
 * @title MetaAssetToken
 * @dev mAsset - Meta Asset Token implementation.
 * Inherits from ERC20.
 * mint and burn functions.
 */

contract MetaAssetToken is ERC20Permit, Ownable {
    // events

    /**
     * @dev Emitted when MassetManager config is changed.
     * @param _newMassetManagerProxy                    Address of new MassetManager proxy.
     */
    event MassetManagerProxyChanged(address indexed _newMassetManagerProxy);

    /**
     * @dev Emitted when Basket Manager config is changed.
     * @param _newBasketManagerProxy                    Address of new Basket Manager proxy.
     */
    event BasketManagerProxyChanged(address indexed _newBasketManagerProxy);

    /**
     * @dev Emitted when transfer  Manager config is changed.
     */
    event TransferWithPermit(address _from, address _to, uint256 _amount);

    // state

    address public massetManagerProxy;
    address public basketManagerProxy;

    // modifiers
    modifier onlyMassetManagerProxy() {
        require(msg.sender == massetManagerProxy, "DLLR:unauthorized MassetManager proxy");
        _;
    }

    modifier requireValidRecipient(address _recipient) {
        require(
            _recipient != address(0) && _recipient != address(this),
            "DLLR: Invalid address. Cannot transfer DLLR to the null address."
        );
        _;
    }

    /**
     * @notice Constructor called on deployment, initiates the contract.
     */
    constructor(
        string memory _tokenName,
        string memory _symbol
    ) ERC20(_tokenName, _symbol) ERC20Permit(_tokenName) {}

    /**
     * @dev getter function of MassetManager implementation address
     *
     * @return MassetManager implementation address
     */
    function massetManagerImplementation() public view virtual returns (address) {
        return IProxy(massetManagerProxy).getProxyImplementation();
    }

    /**
     * @dev getter function of basket manager implementation address
     *
     * @return basket manager implementation address
     */
    function basketManagerImplementation() public view virtual returns (address) {
        return IProxy(basketManagerProxy).getProxyImplementation();
    }

    /**
     * @notice setMassetManagerProxy sets the MassetManager proxy address
     * @param _massetManagerProxy The address of the MassetManager proxy contract
     */
    function setMassetManagerProxy(address _massetManagerProxy) external onlyOwner {
        require(_massetManagerProxy != address(0), "invalid MassetManager proxy address");
        massetManagerProxy = _massetManagerProxy;

        emit MassetManagerProxyChanged(massetManagerProxy);
    }

    /**
     * @notice setBasketManagerConfig sets the Basket Manager proxy address
     * @param _basketManagerProxy The address of the Basket Manager proxy contract
     */
    function setBasketManagerProxy(address _basketManagerProxy) external onlyOwner {
        require(_basketManagerProxy != address(0), "invalid address");
        basketManagerProxy = _basketManagerProxy;
        emit BasketManagerProxyChanged(basketManagerProxy);
    }

    /**
     * @notice Creates new tokens and sends them to the recipient.
     * @notice Can be minted only by the MassetManager proxy contract.
     *
     * @param _account The recipient address to get the minted tokens.
     * @param _amount The amount of tokens to be minted.
     */
    function mint(address _account, uint256 _amount) external onlyMassetManagerProxy {
        _mint(_account, _amount);
    }

    /**
     * @notice Burns tokens for the given account.
     * @notice Can be burned only by the MassetManager proxy contract.
     *
     * @param _account The recipient address to get the minted tokens.
     * @param _amount The amount of tokens to be minted.
     */
    function burn(address _account, uint256 _amount) external onlyMassetManagerProxy {
        _burn(_account, _amount);
    }

    /**
     * @notice Only owner who can transfer the token.
     * @notice destination cannot be:
     * - zero (0x0) address.
     *
     * @param _recipient Recipient of the token.
     * @param _amount The amount of token that will be transferred.
     *
     * @return true / false.
     */
    function transfer(
        address _recipient,
        uint256 _amount
    ) public override requireValidRecipient(_recipient) returns (bool) {
        _transfer(_msgSender(), _recipient, _amount);
        return true;
    }

    /**
     * @notice Only owner who can transfer the token.
     * @notice destination cannot be:
     * - zero (0x0) address.
     *
     * @param _from Sender of the token.
     * @param _to Recipient of the token.
     * @param _amount The amount of token that will be transferred.
     *
     * @return true / false.
     */
    function transferFrom(
        address _from,
        address _to,
        uint256 _amount
    ) public override requireValidRecipient(_to) returns (bool) {
        _approve(_from, msg.sender, allowance(_from, msg.sender) - _amount);
        _transfer(_from, _to, _amount);
        return true;
    }

    /**
     * @notice transfer utilizing EIP-2612, to reduce the additional sending transaction for doing the approval to the spender.
     *
     * @notice destination cannot be:
     * - zero (0x0) address.
     *
     * @dev By calling this function, the allowance will be overwritten by the total amount.
     *
     * @param _from Owner of the token.
     * @param _to Recipient of the token.
     * @param _amount The amount of the token that will be transferred.
     * @param _deadline Expiration time of the signature.
     * @param _v Last 1 byte of ECDSA signature.
     * @param _r First 32 bytes of ECDSA signature.
     * @param _s 32 bytes after _r in ECDSA signature.
     */
    function transferWithPermit(
        address _from,
        address _to,
        uint256 _amount,
        uint256 _deadline,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external requireValidRecipient(_to) {
        permit(_from, msg.sender, _amount, _deadline, _v, _r, _s);
        require(
            transferFrom(_from, _to, _amount),
            "MetaAssetToken::transferWithPermit: transfer failed"
        );

        emit TransferWithPermit(_from, _to, _amount);
    }

    /**
     * @notice Approves and then calls the receiving contract.
     * Useful to encapsulate sending tokens to a contract in one call.
     * Solidity has no native way to send tokens to contracts.
     * ERC-20 tokens require approval to be spent by third parties, such as a contract in this case.
     * @param _spender The contract address to spend the tokens.
     * @param _amount The amount of tokens to be sent.
     * @param _data Parameters for the contract call, such as endpoint signature.
     */
    function approveAndCall(address _spender, uint256 _amount, bytes calldata _data) external {
        approve(_spender, _amount);
        IApproveAndCall(_spender).receiveApproval(msg.sender, _amount, address(this), _data);
    }

    /**
     * @dev to support EIP712, will need the token contract to return the chain id.
     *
     * @return chain id.
     *
     */
    function getChainId() external view returns (uint256) {
        return block.chainid;
    }
}
","keccak256":"0x887c09ca5cf835d67db8389c8f4ab4fc2689444bb5806370431e1a18900b66d7","license":"MIT"},"contracts/vault/FeesVault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title FeesVault
 * @dev Contract is used to store fees.
 */
contract FeesVault is OwnableUpgradeable {
    using SafeERC20 for IERC20;

    function initialize() external initializer {
        __Ownable_init_unchained();
    }

    /**
     * @dev Withdraw collected fees.
     * @param _token            Address of token to withdraw.
     * @param _amount           Amount to withdraw.
     * @param _recipient        Recipient of withdrawn tokens.
     */
    function withdraw(address _token, uint256 _amount, address _recipient) public onlyOwner {
        IERC20(_token).safeTransfer(_recipient, _amount);
    }
}
